class TimeTotal extends HTMLElement{constructor(){super(),this.attachShadow({mode:"open"})}connectedCallback(){this.showTotalTime()}getTotalMinutes=e=>[...e].reduce((e,t)=>{const n=parseInt(t.getAttribute("datetime").slice(1,-1));return e+(isNaN(n)?0:n)},0);formatTime=e=>({hours:Math.floor(e/60),minutes:e%60});showTotalTime=()=>{const s=document.querySelectorAll(".c-block__time"),o=this.getTotalMinutes(s),{hours:t,minutes:n}=this.formatTime(o),e=document.createElement("time");this.shadowRoot.appendChild(e),e.textContent=`${t}h ${n}m`,e.setAttribute("datetime",`PT${t}H${n}M`),e.setAttribute("title","Total time estimate")}}customElements.define("time-total",TimeTotal)